package Batch37Assignments;

public class Amt109_AssertKeywordwith2ndSyntax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("WAP to use Assert keyword with assert syntax2");
		int no1=100;
		int no2=200;
		assert no1>no2: "no1 is lesser"; //false
		System.out.println(no1+no2);

	}

}
