package Batch37Assignments;

public class Amt4_CrtngMnyStaticMthdAndCalngMMthd { //creating many static method and calling Inside Main Method

	static void PrintUrName()
	{
		System.out.println("My Name is Manohar Raju");	
	}
	static void PrintUrFatherName()
	{
		System.out.println("My Father Name is Raju");
	}
	static void PrintUrMotherName()
	{
		System.out.println("My Mother Name is Vijaya");
	}
	static void PrintUrSisterName()
	{
		System.out.println("My Sister Name is Ananthi");
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Main Method");
		PrintUrName();
		PrintUrFatherName();
		PrintUrMotherName();
		PrintUrSisterName();

	}

}
