package Batch37Assignments;

public class Amt9_CrtngAdd_Sub_Mul_DivPgm {
	
	static void Addition()
	{
		int a=10,b=20,Add=a+b;
		System.out.println("Addition Value is: " +Add);
		
	}
	
	static void Subtraction()
	{
		int a=10,b=20,Sub=a-b;
		System.out.println("Subtraction Value is: " +Sub);
	}
	
	static void Multiplication()
	{
		int a=10,b=20,Mul=a*b;
		System.out.println("Multiplication Value is: " +Mul);
	}
	
	static void Divison()
	{
		double a=10,b=20,Div=a/b;
		System.out.println("Divson Value is: " +Div);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Main Method");
		Addition();
		Subtraction();
		Multiplication();
		Divison();

	}

}
